import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import PostContext from '../../context/post/postContext';
import AuthContext from '../../context/auth/authContext';
import AlertContext from '../../context/alert/alertContext';

const CreatePost = () => {
  const postContext = useContext(PostContext);
  const authContext = useContext(AuthContext);
  const alertContext = useContext(AlertContext);

  const { addPost } = postContext;
  const { isAuthenticated } = authContext;
  const { setAlert } = alertContext;

  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated) {
      setAlert('Please log in to create a post', 'danger');
      navigate('/login');
    }
    // eslint-disable-next-line
  }, [isAuthenticated, navigate]);

  const [post, setPost] = useState({
    title: '',
    content: '',
    image: null
  });

  const [imagePreview, setImagePreview] = useState(null);

  const { title, content } = post;

  const onChange = e => {
    if (e.target.name === 'image') {
      const file = e.target.files[0];
      if (file) {
        setPost({ ...post, image: file });
        setImagePreview(URL.createObjectURL(file));
      }
    } else {
      setPost({ ...post, [e.target.name]: e.target.value });
    }
  };

  const onSubmit = e => {
    e.preventDefault();
    if (title.trim() === '' || content.trim() === '') {
      setAlert('Please fill in both title and content', 'danger');
    } else {
      // Create FormData to handle file upload
      const formData = new FormData();
      formData.append('title', title);
      formData.append('content', content);
      if (post.image) {
        formData.append('image', post.image);
      }

      addPost(formData);
      setAlert('Post Created Successfully', 'success');
      navigate('/');
    }
  };

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-lg shadow-md overflow-hidden mb-8">
        <div className="p-6">
          <h1 className="text-2xl font-bold text-gray-900 mb-6 text-center">Create New Post</h1>
          <form onSubmit={onSubmit}>
            <div className="mb-4">
              <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">
                Title
              </label>
              <input
                id="title"
                type="text"
                placeholder="Enter post title"
                name="title"
                value={title}
                onChange={onChange}
                className="w-full px-3 py-2 text-gray-700 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="image" className="block text-sm font-medium text-gray-700 mb-1">
                Image (optional)
              </label>
              <input
                id="image"
                type="file"
                name="image"
                onChange={onChange}
                accept="image/*"
                className="w-full px-3 py-2 text-gray-700 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
              {imagePreview && (
                <div className="mt-2">
                  <img 
                    src={imagePreview} 
                    alt="Preview" 
                    className="max-h-48 rounded-md object-cover"
                  />
                </div>
              )}
            </div>

            <div className="mb-6">
              <label htmlFor="content" className="block text-sm font-medium text-gray-700 mb-1">
                Content
              </label>
              <textarea
                id="content"
                placeholder="Write your post content here..."
                name="content"
                value={content}
                onChange={onChange}
                rows="10"
                className="w-full px-3 py-2 text-gray-700 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
                required
              ></textarea>
            </div>

            <div className="flex justify-end">
              <button
                type="submit"
                className="px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
              >
                Create Post
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CreatePost;
